package PortoSeguro;

import java.util.Scanner;

public class CorrecaoDecisao {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// faça um progra,a que leia um nunmero inteiro e mostre uma mensagem na tela
		// indicando se este numero é par ou impar, e se é positivo ou negativo
		
		int numero;
		
		Scanner leia = new Scanner(System.in);
			System.out.println("\n Entre com um número inteiro: ");
			numero = leia.nextInt();
		
			if( numero % 2 == 0 ) 
			{ 
				if( numero >=0) 
				{ 
					System.out.println("\n Este número é par e positivo");
				}
				else 
				{
					System.out.println("\n Este número é par e negativo");
				}
			}
			else{ 
				if( numero >=0) 
				{ 
					System.out.println("\n Este número é impar e positivo");
				}
				else 
				{
					System.out.println("\n Este número é impar e negativo");
				}
		
			}		
		
	}

}
